INSERT INTO `trans_member` VALUES (429, 0, '7dacd9dca729bcef85a58805351b5497b6c7052e', 'Noriel', 'Jarito', 'Magallanes StreetPambujan, Northern Samar', '', '', '6413', 'PH', 'ironoriel@yahoo.com', '', '+639289604679', '<p><span>Founded Samar Sine Sociedad, Inc. in 2010 a cultural organization dedicated in the promotion and development of culture and arts in the Eastern part of the Philippines and run a Regional film festival called Pambujan Film Festival on its 3<sup>rd</sup> year. His first full length film <em>Rindido</em> (Rage) was one of the finalists in the 36<sup>th</sup> Metro Manila Film Festival&rsquo;s 1<sup>st</sup> ever digital category held in December 2010. His documentary &lsquo;Bingo&rsquo; was shown in 13<sup>th</sup> Lyon Asian Film Festival (France, 2007), competed in Babelgum online film festival, won honorable mention in 20<sup>th</sup> CCP (Cultural Center of the Philippines) alternative film competition (Philippines, 2008). His first short film OFW: Bagong Bayani o Baliw na Bayani (Overseas Filipino Worker: New Heroes or Mad Heroes) competed in 11<sup>th</sup> Int&rsquo;l Panorama of Independent Filmmakers (Greece, 2009), <span>Tehran 11th Int&rsquo;l Short Film Festival (Iran, <span>2006). Ba&rsquo;t &lsquo;Man (Why?) another short film about migrant workers won 2<sup>nd</sup> prize in the </span></span>21<sup>st</sup> CCP alternative film competition (Philippines, 2009). He graduated Fine Arts major in advertising from Far Eastern University, Manila in 1993 and worked as a Graphic Designer in Saudi Arabia for international ad agencies from 1996 to 2001. He just currently passed the board exam for professional teachers. He lives in Pambujan, Northern Samar with his wife Tina and stepson Kim. <span>&nbsp;</span></span></p>', 'ironoriel', '68f77865a692e1e1ec9279d8fae4a2d4', '7dacd9dca729bcef85a58805351b5497b6c7052eNoriel Jarito_Director.jpg', 'Noriel', 'Jarito', 'Magallanes Street, Pambujan, Northern Samar', 'PH', '', 'Noriel', 'Jarito', 'Magallanes Street, Pambujan, Northern Samar', 'PH', '', 0, 0, 0, 'Y', '', 1325749460, 1325749746, 0, 0, 'y', 'n', 0, 'user');
